import React, { useState, useCallback } from "react";
import PropTypes from "prop-types";

function PostImages({ images }) {
  const [ShowImagesZoom, setShowImagesZoom] = useState("");
  const onZoom = useCallback(() => {
    setShowImagesZoom(true);
  }, []);

  if (images.length === 1) {
    return (
      <>
        <img src={images[0].src} alt={images[0].src} onClick={onZoom} />
      </>
    );
  }
  if (images.length === 2) {
    return (
      <>
        <div>
          <img
            role="presentation"
            src={images[0].src}
            alt={images[0].src}
            width="50%"
            onClick={onZoom}
          />
          <img
            role="presentation"
            src={images[1].src}
            alt={images[1].src}
            width="50%"
            onClick={onZoom}
          />
        </div>
      </>
    );
  } else {
    return (
      <>
        <img
          src={images[0].src}
          alt={images[0].src}
          style={{ width: "20%" }}
          role="presentation"
          onClick={onZoom}
        />
        {images.length - 1}개의 사진 더보기
      </>
    );
  }
}

PostImages.PropTypes = {
  //배열 안에 객체 [{},{},{} ] 타입이 맞나 확인
  images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
