import React, { useState, useCallback } from "react";

//custom Hook으로 만들기

export default function (initialValue = null) {
  console.log("몇번 렌더링?");
  const [Value, setValue] = useState(initialValue);
  const handler = useEffect((e) => {
    setValue(e.target.value);
    console.log("장난하나");
  }, []);

  return [Value, handler];
}
